		[[CeL.to_CSV_String([["a", "b"], ['"e"', 2], [3, 4]]), '"a","b"\n"""e""","2"\n"3","4"']],
		[[CeL.to_CSV_String([["a", "b"], ['"e"', 2], [3, 4]], { no_text_qualifier: 'auto' }), 'a,b\n"""e""",2\n3,4']],
		[[CeL.to_CSV_String([["a", "b"], ['"e"', 2], [3, 4]], { no_text_qualifier: true }), 'a,b\n"e",2\n3,4']],
