		assert(['hostname.wiki', uri.hostname], 'CeL.net.URI() #3 of ' + href);

		href = 'http://hostname.org/測試編碼/?編=碼&!+%20%%26%3D=$%24%%25%26%3D*#a#$%^&'; url = new URL(href); uri = new CeL.URI(href);
