node_info.color = {
	test: 'fg=yellow',
	passed: 'fg=green;bg=white',
	error: 'fg=red;bg=white'
};

function node_info(messages) {
	if (CeL.SGR.CSI !== CeL.SGR.default_CSI) {
		CeL.info(messages);
		return;
	}

	var matched;
	if (typeof messages === 'string'
		&& (matched = messages.match(/^([a-z\d\-]+)([:\s].+)$/i))
		&& matched[1].toLowerCase() !== 'all') {
		messages = [
			'',
			node_info.color[matched[1].toLowerCase()]
			|| 'fg=black;bg=white', matched[1], '-fg;-bg',
			matched[2]];
	}
	CeL.info(new CeL.SGR(messages).toString());
}


// return has error
function setup_test(type) {
	if (type && !still_running[type]) {
		still_running[type] = true;
		++still_running.left_count;
		return false;
	}
	return true;
}

function finish_test(type) {
	//console.trace(all_error_count);
	if (type && still_running[type]) {
		delete still_running[type];
		--still_running.left_count;
	}
	if (!still_running.all_configured || still_running.left_count !== 0) {
		return;
	}

	// 耗時，經過時間 Takes ? ms
	var elapsed_message = ' Elapsed time: '
		+ Math.round((Date.now() - test_start_time) / 1000) + ' s.';

	if (all_error_count === 0) {
		// console.trace(still_running);
		node_info(['CeJS: ', 'fg=green;bg=white', 'All tests passed. 測試全部通過。',
			'-fg;-bg', elapsed_message]);
		// normal done. No error.
		return;
	}

	// add gettext format [error]
	CeL.gettext.conversion['error'] = ['no %n', '1 %n', '%d %ns'];
	node_info(['CeJS: ', 'fg=red;bg=white', CeL.gettext('All %error@1 occurred.', all_error_count),
		'-fg;-bg']);
	if (all_error_count > 0) {
		var error_message = CeL.gettext('All %error@1.', all_error_count) + elapsed_message;
		throw new Error(error_message);
	}
}

var all_test_items = {
	console: ['interact.console', test_console],
	locale: ['application.locale', test_locale],
	// 基本的功能先作測試。
	base: test_base,
	compatibility: test_compatibility,
	native: ['data.native', test_native],
	data: [['data', 'data.Number_range_set'], test_data],
	date: ['data.date', test_date],
	character: ['data.character', test_character],
	encoding: ['application.locale.encoding', test_encoding],
	code: ['data.code', test_code],
	check: ['data.check', test_check],
	math: [['data.math.rational', 'data.math.quadratic'], test_math],
	Hamming: ['data.math.Hamming', test_Hamming],
	quantity: ['data.quantity', test_quantity],
	CSV: ['data.CSV', test_CSV],
	XML: ['data.XML', test_XML],
	DOM: ['interact.DOM', test_DOM],
	numeral: ['data.numeral', test_numeral],
	net: ['data.character', 'application.net', test_net],
	astronomy: ['application.astronomy', test_astronomy],
	calendar: ['data.date.calendar', test_calendar],
	era: ['data.date.era', test_era],
	wiki: [[
		'application.net.wiki',
		'application.net.wiki.data',

		'application.net.wiki.template_functions'
	], test_wiki]
};

function do_test() {
	// CeL.assert([ typeof CeL.assert, 'function' ], 'CeL.assert is working.');
	CeL.env.ignore_COM_error = true;
	CeL.set_debug(test_debug_level);
	// 即時顯示，不延遲顯示
	CeL.Log.interval = 0;

	//console.log(CeL.env.argv);
	var test_items = [];
	if (CeL.env.argv.length === 2) {
		for (var item in all_test_items)
			test_items.push(item);
	} else {
		//`node "_test suite\test.js" wiki`
		for (var index = 2; index < CeL.env.argv.length; index++) {
			var item = CeL.env.argv[index], matched;
			if (item in all_test_items) {
				test_items.push(item);
			} else if (matched = item.match(/^(?:set_)?debug=(\d)$/)) {
				CeL.set_debug(+matched[1]);
			}
		}
	}

	console.log('do_test: ' + test_items.length + ' item(s) to test: ' + test_items.join(', '));

	for (var index = 0; index < test_items.length; index++) {
		var item = test_items[index];
		test_items[index] = all_test_items[item];
	}

	CeL.run(
		// 測試期間時需要用到的功能先作測試。這些不可 comment out。
		'interact.console', 'application.locale',
		//
		//function () { CeL.set_debug(0); },
		//
		test_items,
		//
		function () {
			still_running.all_configured = true;
			CeL.info('所有測試設定完畢。 Waiting for asynchronous tests...');
		});
}

CeL.env.no_catch = true;
// CeL.set_debug(3);
CeL.run(['application.debug', 'application.debug.log'], do_test);
