	// 當曆法接著將起始的年次/月次/起始日碼=當月之日數 data;
	'2/1/2=29;30;29';
	// 可使用之指定法: 月,年/,/日,/月/日,年/月/,年/月/日. e.g.,
	// =日數 data; : 1年/1月/1日起始
	'=29;30;29';
	// 月次=日數 data; : 1年/月次/1日起始
	'2=29;30;29;30';
	// 年次/=日數 data; : 年次/1月/1日起始
	'2/=30;2=29;閏2=30;3=29;';
	// /日次=日數 data; : 1年/1月/日次起始
	'/2=30;';
	// 年次/月次/=日數 data; : 年次/月次/1日起始
	'2/1/=30;2=29;閏2=30;3=29;' + ';11=30;12=29;11=30;12=29;後12=29	1=29;';
