CeL.era.pass_on.step = function() {
	// 還需要等待載入的國家。
	var queue = CeL.era.load_queue,
	// 因為載入時間較長，使用此功能可降低反應倦怠感，改善體驗。
	loaded = typeof CeL.env.era_data_load === 'function'
			&& CeL.env.era_data_load;

	if (Array.isArray(queue) && queue.length > 0) {
		var country = queue.shift();
		// console.info(country);

		CeL.era.set(this.countries[country], {
			國家 : country
		});
	}

	if (queue.length > 0) {
		if (loaded) {
			// CeL.log('Era data of country [' + country + '] loaded.');
			loaded(country, queue);
		}

		setTimeout(this.step, 0);
		return;
	}

	// done. 已載入曆數資料。
	CeL.era.loaded = true;

	if (loaded)
		loaded(country);

	// Release memory. 釋放被占用的記憶體.
	// this.CeL_era_data = this.step = this.queue = null;

	try {
		// delete this.CeL_era_data;
		// delete this.step;
		// delete this.queue;
		delete CeL.era.pass_on;
		delete CeL.era.load_queue;
	} catch (e) {
		// IE10 不允許 <code>delete this.*;</code>。
		// Error 445 [TypeError] (facility code 10): 物件不支援此動作
	}

}.bind(CeL.era.pass_on);

if (typeof CeL === 'function') {
	CeL.run('data.date.era', function() {
		// delete this.CeL_era_data.data;

		// 準備開始載入曆數資料。
		CeL.era.loaded = false;

		var queue = CeL.era.load_queue = [];
		// 國家
		for ( var country in this.countries)
			queue.push(country);

		if (typeof CeL.env.era_data_load === 'function') {
			// 第一次呼叫 callback。
			// 在載入era模組之前設定好，可以用來篩選需要載入的國家。
			CeL.env.era_data_load(this.countries, queue);
		}
		// console.log('Load ' + queue.join(', '));

		setTimeout(this.step, 0);

	}.bind(CeL.era.pass_on));

} else {
	CeL.era.pass_on.step();
}
